import React from 'react'

function Sidebar({ topAnime }) {
	return (
		<aside>
			<nav>
				<h3>Top Anime</h3>
				{topAnime.map(anime => (
					<a 
						href={anime.url} 
						target="_blank"
						key={anime.mal_id} 
						rel="noreferrer">
						{ anime.title }
						<img 
      src="https://images.pexels.com/photos/20787/pexels-photo.jpg?auto=compress&cs=tinysrgb&h=350"
      alt="new"
      />
					</a>
				))}
			</nav>
		</aside>
	)
}

export default Sidebar
